import uuid from 'uuid';
import { GET_ITEMS, ADD_ITEM, DELETE_ITEM  } from '../actions/types';


const initialState = {
  items: [],
  item: {}
}

export default function(state = initialState, action) {
  switch(action.type){
    case FETCH_POSTS:
    console.log('reducer');
    return {
      ...state,
      items: action.payload
    }
    case NEW_POST:
      return {
        ...state,
        item: action.payload
      }
    default:
      return state;
  }
}
