import { GET_ITEMS, ADD_ITEM, DELETE_ITEM, ITEMS_LOADING  } from './types';

export const getItems = () => dispatch =>  {
  dispatch(setItemsLoading());
  axios
    .get('/api.items')
    .then(res =>
      dispatch({
        type: GET_ITEMS,
        payload: res.data
      })
    )
}

export const deleteItem = (id) => {
  return {
    type: DELETE_ITEM,
    payload: id
  };
}
export const addItem = (item) => {
  return {
    type: ADD_ITEM,
    payload: item
  };
}
export const setItemsLoading = () => {
  return {
    type: ITEMS_LOADING
  };
}
