import uuid from 'uuid';
import { GET_ITEMS, ADD_ITEM, DELETE_ITEM  } from '../actions/types';


const initialState = {
  items:[
    { id:uuid(), name: 'Chickpeas' },
    { id:uuid(), name: 'Cherries' },
    { id:uuid(), name: 'Rice' },
    { id:uuid(), name: 'Clams' }
  ]
}

export default function(state = initialState, action) {
  switch(action.type){
    case GET_ITEMS:
    return {
      ...state,
      items: action.payload
    }
    // case ADD_ITEM:
    //   return {
    //     ...state,
    //     item: action.payload
    //   }
    //   case DELETE_ITEM:
    //     return {
    //       ...state,
    //       item: action.payload
    //     }
    default:
      return state;
  }
}
